mapper.select:
select

from risk_identify_record


    customerid = #{customerid}


    checkorderid = #{checkorderid}


    AND interfacename = #{interfacename}



    AND md5checksum1 = #{md5checksum1}


    AND md5checksum2 = #{md5checksum2}



    AND number = #{number}


    AND name = #{name}


    AND phone = #{phone}
